mapper.select:
 
select distinct a.ono,uuid,osuju,a.cname,otext,oday,pcount, (select pproduct from cart limit 1)as pproduct, pcount ,scount 
from orderr a join cart b on a.ono = b.ono 
where osuju = "수주 확인" or b.scount ="제품 부족" order by a.ono asc;

mapper.update:
update cart a, orderr b set scount ="제품 부족", 
pcount = (select pcount from (select (pcount - #{scount}) pcount from orderr where ono = #{ono})as pcount) 
where b.ono = #{ono} and pproduct = #{pproduct}
